import React, { useState, useEffect, useCallback,useRef } from "react";
import axios from "axios";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const itemsPerPage = 10;
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const intervalRef = useRef(null);
  const [isFetching, setIsFetching] = useState(true); 

  const getUsers = useCallback(async () => {
    try {
      const response = await axios.get("http://localhost:5000/data", {
        params: { page, limit: itemsPerPage, startDate, endDate },
      });
      setUsers(response.data);
    } catch (error) {
      console.log(error);
    }
  }, [page, startDate, endDate]);  

    useEffect(() => {
    if (isFetching) {
      getUsers();
      intervalRef.current = setInterval(getUsers, 1000);
    } else {
      // If fetching is stopped, clear the interval
      clearInterval(intervalRef.current);
    }

    return () => {
      clearInterval(intervalRef.current);
    };
  }, [getUsers, isFetching]);

  useEffect(() => {
    // If the users array is empty and not on the first page, set the page to 1
    if (users.length <= 10  && page !== 1) {
      setPage(1);
    }
  }, [users, page]);

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/data/${id}`);
      getUsers();
    } catch (error) {
      console.log(error);
    }
  };

  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handlePreviousPage = () => {
    setPage((prevPage) => prevPage - 1);
  };

  const startIdx = (page - 1) * itemsPerPage;
  const endIdx = startIdx + itemsPerPage;

  const handleStartDateChange = (event) => {
    setStartDate(new Date(event.target.value));
  };
  
  const handleEndDateChange = (event) => {
    setEndDate(new Date(event.target.value));
  };  

  const handleResetFilter = () => {
    setStartDate(null);
    setEndDate(null);
  };

  const handleStopFetching = () => {
    setIsFetching((prevFetching) => !prevFetching);
  };

  return (
<div style={styles.container}>
      <div style={styles.titleContainer}>
        <h1 className="title is-4">Log Monitoring Dashboard</h1>
      </div>
      <div style={styles.filterContainer}>
      <label>Start Date: </label>
      <input type="date" value={startDate ? startDate.toISOString().slice(0, 10) : ""} onChange={handleStartDateChange} />
      <span style={{ marginLeft: "10px", marginRight: "10px" }}></span>{" "}
      <label>End Date: </label>
      <input type="date" value={endDate ? endDate.toISOString().slice(0, 10) : ""} onChange={handleEndDateChange} />
      <span style={{ marginLeft: "10px", marginRight: "10px" }}></span>{" "}
      <button onClick={handleResetFilter} className="button is-primary is-small">
        Reset Filter
      </button>
      <span style={{ marginLeft: "10px", marginRight: "10px" }}></span>{" "}
      <button onClick={handleStopFetching} className="button is-danger is-small">
          {isFetching ? "Stop Fetching" : "Continue Fetching"}
      </button>
      <label> Page :{page}</label>
      <label> User length :{users.length}</label>
    </div>
      <div style={styles.tableContainer}>
        <table className="table is-striped is-fullwidth mt-2">
            <thead>
              <tr>
                {/* <th>No</th> */}
                <th>Sumber</th>
                <th>Timestamp</th>
                <th>App_name</th>
                <th>Pengukuran</th>
                <th>Value</th>
                <th>Unit</th>
                <th>Satuan</th>
                <th>Status</th>
                <th>status_reading</th>
              </tr>
            </thead>
            <tbody>
              {users.slice(startIdx, endIdx).map((user, index) => (
                <tr key={user._id}>
                  {/* <td>{startIdx + index + 1}</td> */}
                  <td>{user.sumber}</td>
                  <td>{user.timestamp.replace("T", " ").replace("Z", "").slice(0, 19)}</td>
                  <td>{user.app_name}</td>
                  <td>{user.pengukuran}</td>
                  <td>{user.value}</td>
                  <td>{user.unit}</td>
                  <td>{user.satuan}</td>
                  <td>{user.status}</td>
                  <td>{user.status_reading}</td>
                  <td>
                    <button
                      onClick={() => deleteUser(user._id)}
                      className="button is-danger is-small"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
            </table>
        {users.length > itemsPerPage && (
          <div style={styles.paginationContainer} className="mt-4">
            <button
              onClick={handlePreviousPage}
              className="button is-primary is-small"
              disabled={page === 1}
            >
              Previous
            </button>{" "}
            <span style={{ marginLeft: "10px", marginRight: "10px" }}></span>{" "}
            <button
              onClick={handleNextPage}
              className="button is-primary is-small"
              disabled={endIdx >= users.length}
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    flexDirection: "column",
    height: "100vh", // Set the height of the container to 100% of the viewport
  },
  titleContainer: {
    flex: 1,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",

  },
  tableContainer: {
    flex: 6,
    display: "flex",
    flexDirection: "column",
  },
  paginationContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
};

export default UserList;
